INSERT INTO `paket` (`id_paket`, `nama_paket`, `ket_paket`, `harga_paket`, `gambar_paket`) VALUES
(8, 'Paket Liburan Tana Toraja', '<p>&quot;Explore Tana Toraja&quot;**</p>\r\n\r\n<p>**Durasi:** 2 Hari 1 Malam</p>\r\n\r\n<p>**Harga:** Rp. 500.000/orang</p>\r\n\r\n<p>**Tanggal Keberangkatan:**<br />\r\n- Keberangkatan: 18&nbsp;Januari 2024</p>\r\n\r\n<p>**Transportasi:**<br />\r\n- Pergi dan Pulang dengan bus AC dari kota asal ke Tana Toraja.</p>\r\n\r\n<p>**Penginapan:**<br />\r\n- Menginap di hotel/bungalow di Tana Toraja selama 1 malam.<br />\r\n- Kamar ber-AC dan dilengkapi dengan fasilitas mandi.</p>\r\n\r\n<p>**Itinerary:**</p>\r\n\r\n<p>**Hari Pertama - Tana Toraja Arrival:**<br />\r\n1. **08.00 - 12.00:** Perjalanan dari kota asal ke Tana Toraja dengan bus AC.<br />\r\n2. **12.00 - 13.00:** Makan siang di rest area perjalanan.<br />\r\n3. **13.00 - 17.00:** Check-in di hotel/bungalow dan istirahat sejenak.<br />\r\n4. **17.00 - 19.00:** Kunjungan pertama ke salah satu objek wisata Tana Toraja (contoh: Londa, Lemo, Kete Kesu).<br />\r\n5. **19.30 - 21.00:** Makan malam di restoran lokal.<br />\r\n6. **21.00:** Kembali ke hotel, istirahat, dan persiapan untuk esok hari.</p>\r\n\r\n<p>**Hari Kedua - Menikmati Keindahan Tana Toraja:**<br />\r\n1. **07.00 - 08.00:** Sarapan di hotel.<br />\r\n2. **08.30 - 11.30:** Kunjungan ke objek wisata lainnya (contoh: Kambira, Suaya, Ke&#39;te Kesu).<br />\r\n3. **12.00 - 13.00:** Makan siang di restoran lokal.<br />\r\n4. **13.30 - 16.00:** Berkeliling dan menikmati keindahan alam Tana Toraja.<br />\r\n5. **16.30 - 18.00:** Kembali ke hotel, bersiap-siap check-out.<br />\r\n6. **18.00 - 19.30:** Makan malam di restoran sebelum perjalanan pulang.<br />\r\n7. **20.00:** Kembali ke kota asal dengan bus AC.</p>\r\n\r\n<p>**Fasilitas yang Termasuk dalam Paket:**<br />\r\n1. Tiket bus PP dari kota asal ke Tana Toraja.<br />\r\n2. Penginapan 1 malam di hotel/bungalow dengan sarapan pagi.<br />\r\n3. Makan siang dan makan malam (sesuai jadwal di atas).<br />\r\n4. Tiket masuk ke objek wisata yang dikunjungi.</p>\r\n\r\n<p>**Fasilitas yang Tidak Termasuk:**<br />\r\n1. Pengeluaran pribadi.<br />\r\n2. Asuransi perjalanan pribadi.<br />\r\n3. Pengeluaran tambahan di luar itinerary.</p>\r\n\r\n<p>**Catatan:**<br />\r\nJangan lupa untuk selalu mengecek ketersediaan dan harga terkini sebelum melakukan pemesanan, serta mengonfirmasi detail paket dengan penyelenggara perjalanan.</p>\r\n', 500000, 'paket1.png'),
(10, 'Paket Wisata Keukenhof Garden', '<p>&quot;Keukenhof Garden Experience&quot;**</p>\r\n\r\n<p>**Durasi:** 2 Hari 1 Malam</p>\r\n\r\n<p>**Harga:** Rp. 3.500.000/orang</p>\r\n\r\n<p>**Tanggal Keberangkatan:**<br />\r\n- Keberangkatan: 12 Maret 2024</p>\r\n\r\n<p>**Transportasi:**<br />\r\n- Pergi dan Pulang dengan transportasi udara ke Amsterdam (kota terdekat dengan Keukenhof).<br />\r\n- Transfer dari Bandara Amsterdam Schiphol ke Keukenhof.</p>\r\n\r\n<p>**Penginapan:**<br />\r\n- Menginap di hotel bintang 3 di dekat Keukenhof selama 1 malam.<br />\r\n- Kamar ber-AC dan dilengkapi dengan fasilitas mandi.</p>\r\n\r\n<p>**Itinerary:**</p>\r\n\r\n<p>**Hari Pertama - Keukenhof Exploration:**<br />\r\n1. **09.00 - 11.00:** Penerbangan menuju Bandara Amsterdam Schiphol.<br />\r\n2. **11.30 - 12.30:** Transfer ke hotel dan check-in.<br />\r\n3. **13.00 - 15.00:** Makan siang di restoran lokal.<br />\r\n4. **15.30 - 18.00:** Kunjungan Keukenhof Garden untuk menikmati keindahan taman bunga (tiket masuk termasuk).<br />\r\n5. **18.30 - 20.00:** Kembali ke hotel dan bersiap untuk makan malam.<br />\r\n6. **20.30 - 22.00:** Makan malam di restoran hotel.</p>\r\n\r\n<p>**Hari Kedua - Wisata Sekitar Keukenhof:**<br />\r\n1. **07.00 - 08.00:** Sarapan di hotel.<br />\r\n2. **08.30 - 11.00:** Kunjungan ke desa-desa terdekat atau objek wisata lainnya di sekitar Keukenhof.<br />\r\n3. **12.00 - 13.00:** Makan siang di restoran lokal.<br />\r\n4. **13.30 - 16.00:** Tur keliling Keukenhof dan informasi lebih lanjut tentang kebun bunga.<br />\r\n5. **16.30 - 18.00:** Kembali ke hotel, bersiap untuk check-out.<br />\r\n6. **18.30 - 20.00:** Makan malam terakhir di restoran lokal sebelum kembali.</p>\r\n\r\n<p>**Fasilitas yang Termasuk dalam Paket:**<br />\r\n1. Tiket pesawat PP.<br />\r\n2. Transfer dari Bandara Amsterdam Schiphol ke hotel.<br />\r\n3. Penginapan 1 malam di hotel bintang 3 dengan sarapan pagi.<br />\r\n4. Makan siang dan makan malam (sesuai jadwal di atas).<br />\r\n5. Tiket masuk Keukenhof Garden.<br />\r\n6. Transportasi selama tur keliling.</p>\r\n\r\n<p>**Fasilitas yang Tidak Termasuk:**<br />\r\n1. Pengeluaran pribadi.<br />\r\n2. Asuransi perjalanan pribadi.</p>\r\n\r\n<p>**Catatan:**</p>\r\n\r\n<p>Pastikan untuk selalu mengecek ketersediaan dan harga terkini sebelum melakukan pemesanan, serta mengonfirmasi detail paket dengan penyelenggara perjalanan.</p>\r\n', 3500000, 'paket2.jpg'),
(11, 'Paket Wisata Disneyland Tokyo', '<p>&quot;Magical Disneyland Tokyo Adventure&quot;**</p>\r\n\r\n<p>**Durasi:** 3 Hari 2 Malam</p>\r\n\r\n<p>**Harga:** Rp. 29.900.000/orang</p>\r\n\r\n<p>**Tanggal Keberangkatan:**<br />\r\n- Keberangkatan: 11 November 2024</p>\r\n\r\n<p>**Transportasi:**<br />\r\n- Pergi dan Pulang dengan penerbangan langsung ke Tokyo.<br />\r\n- Transfer dari Bandara Internasional Narita ke hotel.</p>\r\n\r\n<p>**Penginapan:**<br />\r\n- Menginap di hotel bintang 4 di dekat Disneyland Tokyo selama 2 malam.<br />\r\n- Kamar ber-AC dan dilengkapi dengan fasilitas mandi.</p>\r\n\r\n<p>**Itinerary:**</p>\r\n\r\n<p>**Hari Pertama - Selamat Datang di Tokyo:**<br />\r\n1. **12.00 - 15.00:** Penerbangan menuju Bandara Internasional Narita.<br />\r\n2. **15.30 - 17.00:** Transfer ke hotel dan check-in.<br />\r\n3. **17.30 - 19.00:** Makan malam di restoran hotel.<br />\r\n4. **19.30 - 22.00:** Istirahat dan persiapan untuk petualangan Disneyland esok hari.</p>\r\n\r\n<p>**Hari Kedua - Disneyland Tokyo Experience:**<br />\r\n1. **07.00 - 08.00:** Sarapan di hotel.<br />\r\n2. **08.30 - 10.00:** Transfer ke Disneyland Tokyo.<br />\r\n3. **10.30 - 18.00:** Nikmati wahana dan pertunjukan Disneyland Tokyo.<br />\r\n4. **18.30 - 20.00:** Kembali ke hotel untuk bersiap-siap makan malam.<br />\r\n5. **20.30 - 22.00:** Makan malam di restoran hotel.</p>\r\n\r\n<p>**Hari Ketiga - Keliling Tokyo dan Kembali:**<br />\r\n1. **07.00 - 08.00:** Sarapan di hotel dan check-out.<br />\r\n2. **08.30 - 11.30:** Tur keliling Tokyo dengan tour guide (mengunjungi beberapa tempat wisata terkenal).<br />\r\n3. **12.00 - 13.30:** Makan siang di restoran lokal.<br />\r\n4. **14.00 - 16.00:** Waktu bebas untuk berbelanja atau mengeksplorasi Tokyo.<br />\r\n5. **16.30 - 18.00:** Kembali ke hotel untuk pengambilan barang dan transfer ke Bandara Narita.<br />\r\n6. **19.30 - 22.00:** Penerbangan pulang.</p>\r\n\r\n<p>**Fasilitas yang Termasuk dalam Paket:**<br />\r\n1. Tiket pesawat PP.<br />\r\n2. Transfer dari Bandara Internasional Narita ke hotel dan sebaliknya.<br />\r\n3. Penginapan 2 malam di hotel bintang 4 dengan sarapan pagi.<br />\r\n4. Makan siang dan makan malam (sesuai jadwal di atas).<br />\r\n5. Tiket masuk Disneyland Tokyo.<br />\r\n6. Transportasi selama tur keliling Tokyo dengan tour guide.</p>\r\n\r\n<p>**Fasilitas yang Tidak Termasuk:**<br />\r\n1. Pengeluaran pribadi.<br />\r\n2. Asuransi perjalanan pribadi.</p>\r\n\r\n<p>**Catatan:**</p>\r\n\r\n<p>Pastikan untuk selalu mengecek ketersediaan dan harga terkini sebelum melakukan pemesanan, serta mengonfirmasi detail paket dengan penyelenggara perjalanan.</p>\r\n', 29900000, 'paket3.jpg');

